mapper.select:
SELECT COUNT(*)
FROM ZakSimMember
WHERE id=#{id} AND password=#{password}

SELECT COUNT(*)
FROM ZakSimMember
WHERE id != 'admin'
AND TO_CHAR(join_date, 'YYYY-MM-DD')  > = #{startDate}
AND TO_CHAR(join_date, 'YYYY-MM-DD')  < = #{endDate}
ORDER BY join_date ASC

SELECT COUNT(*) FROM ZakSimMember
WHERE member_type = 'zaksim'
AND id = #{id}
AND password = #{password}

SELECT * FROM ZakSimMember
WHERE member_type = 'zaksim'
AND id = #{id}

SELECT id FROM ZakSimMember
WHERE member_type = 'zaksim'
AND name = #{name}
AND email = #{email}

SELECT COUNT(*) FROM ZakSimMember
WHERE member_type = 'zaksim'
AND id = #{id}
AND name = #{name}
AND email = #{email}

mapper.update:
UPDATE ZakSimMember
SET password = #{ranPw}
WHERE member_type = 'zaksim' AND id = #{id}

mapper.select:
SELECT COUNT(*) FROM ZakSimMember
WHERE member_type = 'zaksim'
AND id = #{id}

SELECT COUNT(*) FROM ZakSimMember
WHERE member_type = 'zaksim'
AND nick = #{nick}
